var bids = datasources["itbit-orderbook-btcusd"]["bids"]
var asks = datasources["itbit-orderbook-btcusd"]["asks"]
var bsum = 0
var asum = 0
var depth = 0.01

var askPrice = parseFloat(asks[0][0])
var bidPrice = parseFloat(bids[0][0])
var price = (askPrice + bidPrice) / 2
var bid1 = price - (depth * price)
var ask1 = price + (depth * price)


for (var i=0; i<bids.length; i++){
    var cprice = bids[i][0]
    // if current orderbook price >= top 1% bid price, add them to liq pool sum
    if (cprice >= bid1){
        bsum += parseFloat(bids[i][1])
    }
}

for (var i=0; i<asks.length; i++){
    var cprice = asks[i][0]
    // if current orderbook price >= top 1% bid price, add them to liq pool sum
    if (cprice <= ask1){
        asum += parseFloat(asks[i][1])
    }
}

return ((asum+bsum) * price).toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,')

