var rstr = '<table style="margin: 10px; width: 80%;border-collapse: collapse;"><tr><th>Amount</th> <th>Tokens</th> <th>AddrTo</th> <th>GasCost</th> <th>Timestamp</th> </tr>'
var price = parseFloat(datasources["eth-price"]["lastPrice"])
for(var x = 0; x < 20; x++){
    var addrTo = datasources["hot-txes-tokens"]["result"][x]["to"]
    var tSymbol = datasources["hot-txes-tokens"]["result"][x]["tokenSymbol"]
    var denom = 1000000000000000000

    if (tSymbol == "HUSD") {denom = 100000000}
    if (addrTo == "0xc5a8859c44ac8aa2169afacf45b87c08593bec10") { continue }

    var gasPrice = parseFloat(datasources["hot-txes-tokens"]["result"][x]["gasPrice"])
    var gasUsed = parseFloat(datasources["hot-txes-tokens"]["result"][x]["gasUsed"])
    var gasCost = (gasUsed*(gasPrice/1000000000000000000)).toFixed(6)
    var gasCostinUSD = (gasCost*price).toFixed(2)

    var milliseconds = parseFloat(datasources["hot-txes-tokens"]["result"][x]["timeStamp"])*1000
    var dateObject = new Date(milliseconds)
    var ts = dateObject.toLocaleString()
    var val = (parseFloat(datasources["hot-txes-tokens"]["result"][x]["value"])/denom).toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,')
    var addrTo4 = addrTo.substr(addrTo.length - 4)

    rstr += '<tr style="border-bottom: 1px solid #aaaaaa;">'+"<td>$"+val + "</td><td>" + tSymbol +"</td><td>"+addrTo4+"</td><td>"+gasCost+" ($ "+gasCostinUSD+")"+"</td><td>" + ts + "</td></tr>"
}
rstr += "</table>"
return rstr

