var tickers = datasources["coingecko-pax"]["tickers"]
var total_vol_usd = 0

for (var i=0; i < tickers.length; i++){
    vol_usd = parseFloat(tickers[i]['converted_volume']['usd'])
    if (tickers[i]['trust_score'] == 'green')
    	total_vol_usd += vol_usd
}

return total_vol_usd.toFixed(1).replace(/\d(?=(\d{3})+\.)/g, '$&,')

var tickers = datasources["coingecko-pax"]["tickers"]
var total_vol_usd = 0
var total_green_market = 0

for (var i=0; i < tickers.length; i++){
    vol_usd = parseFloat(tickers[i]['converted_volume']['usd'])
    if (tickers[i]['trust_score'] == 'green') {
        total_vol_usd += vol_usd
        total_green_market += 1
    }

}//end for

return total_green_market

